  baseCreditCost: 3,
  estimatedTimeSeconds: 40,
  costMultipliers: {
    resolution: { "1K": 1, "2K": 1.5 },
  },
  apiEndpoint: "/api/v1/jobs/createTask",
  payloadStructure: "wrapper",
  maxImages: 8,
