  costMultipliers: {
    resolution: { "1K": 1, "2K": 1, "4K": 1.333 },
  },
  apiEndpoint: "/api/v1/jobs/createTask",
  payloadStructure: "wrapper",
  maxImages: 8,
  defaultOutputs: 1,

  // UI metadata
  isActive: true,
  logoUrl: "/logos/google.png",
  modelFamily: "Google",
  variantName: "Nano Banana Pro",
  displayOrderInFamily: 1,

  // Lock system
  isLocked: true,
