import { getGenerationType } from '@/lib/models/registry';
import type { ExecuteGenerationParams } from "@/lib/generation/executeGeneration";
import { supabase } from "@/integrations/supabase/client";
import { reserveCredits } from "@/lib/models/creditDeduction";
import { getKieApiKey as getCentralKieApiKey } from "../getKieApiKey";

export const MODEL_CONFIG = {
  modelId: "recraft/crisp-upscale",
  recordId: "00ef3f28-4fab-4244-b93f-0ba48641fcbd",
  modelName: "Crisp Image Upscale",
  provider: "kie_ai",
  contentType: "image_editing",
  use_api_key: "KIE_AI_API_KEY_IMAGE_EDITING",
  baseCreditCost: 0.25,
  estimatedTimeSeconds: 20,
  costMultipliers: {},
  apiEndpoint: "/api/v1/jobs/createTask",
  payloadStructure: "wrapper",
  maxImages: 1,
  defaultOutputs: 1,

  // UI metadata
  isActive: true,
  logoUrl: "/logos/recraft.png",
  modelFamily: "Kie Ai",
  variantName: "Crisp Image Upscale",
  displayOrderInFamily: 2,

  // Lock system
  isLocked: true,
  lockedFilePath: "src/lib/models/locked/image_editing/Recraft_Crisp_Upscale.ts"
} as const;

export const SCHEMA = {
  type: "object",
  properties: {
    image: {
      type: "string",
      description: "Image to upscale. Accepted types: image/jpeg, image/png, image/webp; Max size: 10.0MB",
      renderer: "image"
    }
  },
  required: ["image"],
  imageInputField: "image",
  "x-order": ["image"]
};

export function validate(inputs: Record<string, any>): { valid: boolean; error?: string } {
  if (!inputs.image) return { valid: false, error: "Image is required" };
  return { valid: true };
}

export function preparePayload(inputs: Record<string, any>): Record<string, any> {
  return {
    modelId: MODEL_CONFIG.modelId,
    input: {
      image: inputs.image
    }
  };
}

export function calculateCost(inputs: Record<string, any>): number {
  return MODEL_CONFIG.baseCreditCost;
}

export async function execute(params: ExecuteGenerationParams): Promise<string> {
  const { prompt, modelParameters, userId, uploadedImageUrls } = params;

  // Map uploaded image URL to the image parameter
  const inputs = {
    ...modelParameters,
    prompt,
    image: uploadedImageUrls[0] || modelParameters.image
  };

  const validation = validate(inputs);
  if (!validation.valid) throw new Error(validation.error);
  
  const cost = calculateCost(inputs);
  await reserveCredits(userId, cost);
  const { data: generation, error: genError } = await supabase
    .from('generations')
    .insert({
      user_id: userId,
      model_id: MODEL_CONFIG.modelId,
      model_record_id: MODEL_CONFIG.recordId,
      prompt: prompt || "Upscale image",
      type: getGenerationType(MODEL_CONFIG.contentType),
      status: 'processing',
      tokens_used: cost,
      settings: modelParameters
    })
    .select()
    .single();

  if (genError || !generation) throw new Error(`Failed to create generation: ${genError?.message}`);

  const payload = preparePayload(inputs);

  try {
    const response = await fetch(`https://api.kie.ai${MODEL_CONFIG.apiEndpoint}`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json', 'Authorization': `Bearer ${await getKieApiKey()}` },
      body: JSON.stringify(payload)
    });

    if (!response.ok) throw new Error(`API call failed: ${response.statusText}`);
    const result = await response.json();
    
    await supabase.from('generations').update({
      provider_task_id: result.taskId,
      provider_request: payload,
      provider_response: result
    }).eq('id', generation.id);

    params.startPolling(generation.id);
    return generation.id;
  } catch (error) {
    await supabase.from('generations').update({ status: 'failed' }).eq('id', generation.id);
    throw error;
  }
}

async function getKieApiKey(): Promise<string> {
  return getCentralKieApiKey(MODEL_CONFIG.modelId, MODEL_CONFIG.recordId, MODEL_CONFIG.use_api_key);
}
