// VALIDATION
export function validate(inputs: Record<string, any>): { valid: boolean; error?: string } {
  if (!inputs.prompt) {
    return { valid: false, error: "Prompt is required" };
  }

  if (inputs.prompt.length > 5000) {
    return { valid: false, error: "Prompt must be 5000 characters or less" };
  }

  if (inputs.negative_prompt && inputs.negative_prompt.length > 5000) {
    return { valid: false, error: "Negative prompt must be 5000 characters or less" };
  }

  if (
    inputs.image_size !== undefined &&
    !["square", "square_hd", "portrait_4_3", "portrait_16_9", "landscape_4_3", "landscape_16_9"].includes(
      inputs.image_size,
    )
  ) {
    return { valid: false, error: "Invalid image_size value" };
  }

  if (inputs.rendering_speed !== undefined && !["TURBO", "BALANCED", "QUALITY"].includes(inputs.rendering_speed)) {
    return { valid: false, error: "rendering_speed must be TURBO, BALANCED, or QUALITY" };
  }

  if (inputs.style !== undefined && !["AUTO", "GENERAL", "REALISTIC", "DESIGN"].includes(inputs.style)) {
    return { valid: false, error: "style must be AUTO, GENERAL, REALISTIC, or DESIGN" };
  }

  if (inputs.num_images !== undefined && !["1", "2", "3", "4"].includes(inputs.num_images)) {
    return { valid: false, error: "num_images must be 1, 2, 3, or 4" };
  }

  return { valid: true };
}

// PAYLOAD PREPARATION
export function preparePayload(inputs: Record<string, any>): Record<string, any> {
  const payload: Record<string, any> = {
    model: MODEL_CONFIG.modelId,
    input: {
      prompt: inputs.prompt,
      image_size: inputs.image_size || "square_hd",
      rendering_speed: inputs.rendering_speed || "BALANCED",
      style: inputs.style || "AUTO",
      num_images: inputs.num_images || "1",
      expand_prompt: inputs.expand_prompt !== false,
    },
  };

  // Add optional parameters only if provided
  if (inputs.negative_prompt) {
    payload.input.negative_prompt = inputs.negative_prompt;
  }

  if (inputs.seed !== undefined && inputs.seed !== null) {
    payload.input.seed = inputs.seed;
  }

  return payload;
}

// COST CALCULATION
export function calculateCost(inputs: Record<string, any>): number {
  const base = MODEL_CONFIG.baseCreditCost;

  // Speed multiplier
  const speedKey = (inputs.rendering_speed || "BALANCED") as keyof typeof MODEL_CONFIG.costMultipliers.rendering_speed;
  const speedMult = MODEL_CONFIG.costMultipliers.rendering_speed[speedKey] || 1;

  // Number of images multiplier
  const numImagesKey = String(inputs.num_images || "1") as keyof typeof MODEL_CONFIG.costMultipliers.num_images;
  const numMult = MODEL_CONFIG.costMultipliers.num_images[numImagesKey] || 1;

  return Math.round(base * speedMult * numMult * 100) / 100;
}

// EXECUTION
export async function execute(params: ExecuteGenerationParams): Promise<string> {
  const { prompt, modelParameters, userId, startPolling } = params;
  const inputs: Record<string, any> = { prompt, ...modelParameters };

  // Validate inputs
  const validation = validate(inputs);
  if (!validation.valid) {
    throw new Error(validation.error);
  }

  // Calculate and reserve credits
  const cost = calculateCost(inputs);
  await reserveCredits(userId, cost);

  // Create generation record with pending status (edge function will process)
  const { data: gen, error } = await supabase
    .from("generations")
    .insert({
      user_id: userId,
      model_id: MODEL_CONFIG.modelId,
      model_record_id: MODEL_CONFIG.recordId,
      type: getGenerationType(MODEL_CONFIG.contentType),
      prompt,
      tokens_used: cost,
      status: GENERATION_STATUS.PENDING,
      settings: sanitizeForStorage(modelParameters),
    })
    .select()
    .single();

  if (error || !gen) {
    throw new Error(`Failed: ${error?.message}`);
  }

  // Call edge function to handle API call server-side
  // This keeps API keys secure and avoids CORS issues
  const { error: funcError } = await supabase.functions.invoke("generate-content", {
    body: {
      generationId: gen.id,
      model_config: MODEL_CONFIG,
      model_schema: SCHEMA,
      prompt,
      custom_parameters: preparePayload(inputs),
    },
  });

  if (funcError) {
    await supabase.from("generations").update({ status: GENERATION_STATUS.FAILED }).eq("id", gen.id);
    throw new Error(`Edge function failed: ${funcError.message}`);
  }

  startPolling(gen.id);
  return gen.id;
}
